mapper.insert:
insert into mp_wechat_qrcode( id, t_create_time, t_create_date, t_status, t_remark, t_month_sharding, t_last_modify_time,
foreign_system_id, foreign_system, foreign_id, foreign_type, qrcode_info, t_ticket, qrcode_url, qrcode_path, wechat_qrcode_url, expire_time, expire_time_str, qrcode_type
) values ( #{id}, #{createTime}, #{createDate}, #{status}, #{remark}, #{monthSharding}, #{lastModifyTime},
#{foreignSystemId}, #{foreignSystem}, #{foreignId}, #{foreignType}, #{qrcodeInfo}, #{ticket}, #{qrcodeUrl}, #{qrcodePath}, #{wechatQrcodeUrl}, #{expireTime}, #{expireTimeStr}, #{qrcodeType}
)

mapper.select:
select id, t_create_time, t_create_date, t_status, t_remark, t_month_sharding, t_last_modify_time,
foreign_system_id, foreign_system, foreign_id, foreign_type, qrcode_info, t_ticket, qrcode_url, qrcode_path, wechat_qrcode_url, expire_time, expire_time_str, qrcode_type
from mp_wechat_qrcode where foreign_system_id = #{foreignSystemId} and foreign_system = #{foreignSystem} and foreign_id = #{foreignId} and foreign_type = #{foreignType} and qrcode_type = #{qrcodeType} and expire_time > #{currentTime}

select id, t_create_time, t_create_date, t_status, t_remark, t_month_sharding, t_last_modify_time,
foreign_system_id, foreign_system, foreign_id, foreign_type, qrcode_info, t_ticket, qrcode_url, qrcode_path, wechat_qrcode_url, expire_time, expire_time_str, qrcode_type
from mp_wechat_qrcode where foreign_system_id = #{foreignSystemId} and foreign_system = #{foreignSystem} and foreign_id = #{foreignId} and foreign_type = #{foreignType} and qrcode_type = #{qrcodeType}
