mapper.select:
select
      id
from mp_wechat_user_info
where foreign_system_id = #{foreignSystemId}
      and foreign_system = #{foreignSystem}
      and open_id = #{openId}
      and foreign_type = #{foreignType}

select
      open_id
from mp_wechat_user_info
where foreign_system_id = #{foreignSystemId}
      and foreign_system = #{foreignSystem}
      and foreign_id = #{foreignId}
      and foreign_type = #{foreignType}
